import React from 'react';
import {
  Container,
  Form,
  FormButton,
  FormContent,
  FormH1,
  FormLabel,
  FormWrap,
  Icon,
  FormInput,
} from '../Signin/SigninElements';

import {projectAuth} from '../../firebase/config'
import { useState } from 'react';

const Signup = () => {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [contact, setContact] = useState('');

  const formSubmit = (e, email, password) => {
    e.preventDefault();
    projectAuth.createUserWithEmailAndPassword(email, password);
    setEmail('');
    setPassword('');
    handleClose();
  };

  return (
    <Container>
      <FormWrap>
        <Icon to='/'>Singulart</Icon>
        <FormContent>
          <Form action=''>
            <FormH1>Sign up to your account.</FormH1>
            <FormLabel htmlFor='username'>Username</FormLabel>
            <FormInput value={username} type='text' required />
            <FormLabel htmlFor='contact'>Contact Number</FormLabel>
            <FormInput value={contact} type='tel' pattern='[0-9]{10}' required />
            <FormLabel htmlFor='email'>Email</FormLabel>
            <FormInput value={email} type='email' required />
            <FormLabel htmlFor='password'>Password</FormLabel>
            <FormInput value={password} type='password' required />
            <FormButton type='submit'>Continue</FormButton>
          </Form>
        </FormContent>
      </FormWrap>
    </Container>
  );
};

export default Signup;
