import React from 'react';
import { useState } from 'react';
import { projectAuth } from '../../firebase/config';
import {
  Container,
  Form,
  FormButton,
  FormContent,
  FormH1,
  FormLabel,
  FormWrap,
  Icon,
  FormInput,
  Text,
} from './SigninElements';

const Signin = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showLogin, setShowLogin] = useState(true);
  const [error, setError] = useState(null);

  const formSubmit = (e, email, password) => {
    e.preventDefault();
    return projectAuth.signInWithEmailAndPassword(email, password).then((cred) => {
      console.log(cred);
      setEmail('');
      setPassword('');
      setShowLogin(false);
      setError(null);
    }).catch(err => {
      setError(err.message);
    })
  }

  const onChangeHandler = (e) => {
    const { name, value } = e.currentTarget;
    if (name === 'email') {
      setEmail(value);
    } else if (name === 'password') {
      setPassword(value);
    }
  };

  return (
    <Container>
      <FormWrap>
        <Icon to='/'>Singulart</Icon>
        <FormContent>
          <Form onSubmit={(e) => formSubmit(e,email,password)}>
            <FormH1>Sign in to your account.</FormH1>
            <FormLabel htmlFor='email'>Email</FormLabel>
            <FormInput value={email} type='email' onChange={(e) => onChangeHandler(e)} required />
            <FormLabel htmlFor='password'>Password</FormLabel>
            <FormInput value={password} type='password' onChange={(e) => onChangeHandler(e)} required />
            <FormButton type='submit'>Continue</FormButton>

          </Form>
        </FormContent>
      </FormWrap>
    </Container>
  );
};

export default Signin;
