import React, {useState} from 'react'
import {Link} from 'react-router-dom';
import { MdKeyboardArrowLeft } from 'react-icons/md';
import Icon2 from "../../images/city.jpeg";
import './index.css';
import BrushIcon from '@material-ui/icons/Brush';
import FavoriteBorderIcon from '@material-ui/icons/FavoriteBorder';
import Axios from 'axios';
import {serverURL} from '../../APIconfig';


function Artwork() {

    const [docs, setDocs] = useState([]);

    Axios.get(serverURL + '/artworks')
    .then((res) => {
        setDocs(res);
    })
    .catch((err)=> {
        console.log(err);
    })



    return (
        <div className='Container'>
            <h1 className='H1'>Gallery</h1>
            <hr class='line'></hr>
            <div class="navigate">
                <Link className='Logo' to='/'><MdKeyboardArrowLeft />Homepage</Link>
            </div>
            <div className='wrap'>
                <div class="wrap_title">
                    Custom
                </div>
                <div class="select_wrap">
                    <ul class="default_option">
                        <li>
                            <div class="option">
                                <p>Sketch</p>
                            </div>
                        </li>
                        <li>
                            <div class="option ">
                                <p>Painting</p>
                            </div>
                        </li>
                        <li>
                            <div class="option">
                                <p>Digital Art</p>
                            </div>
                        </li>
                    </ul>
                </div>
            </div>
            <div class="Wrapper">
                {docs && docs.map((doc) => (
                    <div class="card" key={doc.art_id}>
                        <img src={doc.art_image}></img>
                            <div class="data">
                                <p class="title">&nbsp;{doc.name} </p>
                                <p class='likes'> &nbsp;by {doc.username} <b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;${doc.price}</b></p>
                            </div>
                    </div>)
                )}
            </div> 
        </div>
    )
}

export default Artwork;
